import React from "react";
import styled from "styled-components";
import bannerBG from "images/banner_bg.png";

const bannerTemp = 'data:image/png;base64,';

const BannerContainer = styled.div`
  position: relative;
  width: 100%;
  transition: all 0.5s;
  font-size: 0;
  @media screen and (orientation: landscape) {
    max-height: 300px;
  }
`;

const Img = styled.img`
  width: 100%;
  height: 10%;
  position: relative;
`;

const BannerText = styled.h2`
  position: absolute;
  bottom: 5%;
  left: 5%;
  text-align: left;
  white-space: pre-wrap;
  color: ${({ theme }) => theme.colorSet.onboard_color_type1};
`;

const Main = styled.div`
  position: relative;
  ${({ theme }) => theme.fontSet.Type_A24};
  font-weight: ${({ theme }) => theme.fontWeight.Bold};
`;

const Sub = styled.div`
  position: relative;
  margin-top: 8px;
  ${({ theme }) => theme.fontSet.Type_A12};
  font-weight: ${({ theme }) => theme.fontWeight.Regular};
`;

/**
 * JavaScript에서 true && expression은 항상 expression으로 평가되고
 *  false && expression은 항상 false로 평가됩니다.

따라서 && 뒤의 엘리먼트는 조건이 true일때 출력이 됩니다. 
조건이 false라면 React는 무시하고 건너뜁니다.

 */
const Banner = ({ bannerData }) => {
  return(
    <BannerContainer className={bannerData ? "" : "banner-box"}>
      <Img src={bannerData ? bannerBG : bannerTemp} alt={`배경 이미지`}/>
      <BannerText>
        <Main>
          {bannerData && bannerData.bannerText.split('<br />').map(( line, id ) => {
            line=line.trimStart();
            return (
                <span key={`banner_${id}`}>{line}<br /></span>
              )
            })
          }
          {!bannerData && (
            <span></span>
          )}
        </Main>
        <Sub className={bannerData ? "" : "banner-text"}>{bannerData ? "미리 살펴볼까요?" : "xxxxxxxxxxxx"}</Sub>
      </BannerText>
    </BannerContainer>
  )
}

export default Banner;